mapper.insert:
INSERT INTO author (
						authorName,
						nationId,
						authorIntro
					) 
			VALUES (	#{authorName},
						#{nationId},
						#{authorIntro}
					)

mapper.select:
SELECT * FROM( SELECT /*+ INDEX_DESC(AUTHOR SYS_C0055680) */ ROWNUM as rn, author.authorId, author.authorName, author.nationId, author.regDate, author.updateDate, nation.nationName FROM author INNER JOIN nation ON author.nationId = nation.nationId WHERE
authorName LIKE '%'||#{keyword}||'%' AND
ROWNUM <= #{pageNum} * #{amount} ) WHERE rn > (#{pageNum}-1) * #{amount}
